  }

  render() {
    return (
      <div>
        <Header score={this.state.score} topScore={this.state.topScore} />
        <div className="container">
            {this.state.chars &&
              this.state.chars.map(char => {
                return (
                  <Character
                    key={char.id}
                    image={char.image}
                    name={char.name}
                  />
                );
              })}
        </div>
      </div>
    );
  }
}

export default Main;
