import React, { Component } from 'react'
import chars from "../data/chars.json"

class Main extends Component {
  componentDidMount(){
    this.shuffle();
  }
  shuffle = chars => {
    if(chars){
      console.log(chars)
      const shuffledChars = chars.slice()
    for (let i = shuffledChars.length - 1; i > 0; i--) {
        const rand = Math.floor(Math.random() * (i + 1));
        [shuffledChars[i], shuffledChars[rand]] = [shuffledChars[rand], shuffledChars[i]];
    }
    return shuffledChars
    }
  }
  render(){
    return (
      <div className="container">
        <div className="row">
        {chars.map(char=>{
          return(
            <div className="col s3">
              <img src={char.image} alt={char.name} className="responsive-img"/>
            </div>
          )
        })}
        </div> 
      </div>
    )
  }
}

export default Main
